package com.java;

public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double x = 2.56;
		double y = 4.765;
		double z = 3.1416;
		
		double suma = x + y + z;
		double resta = y - z - x;
		double mult = x * y * z;
		double div = y / z / x;
		
		
		System.out.println("La suma de 3 valores doubles es: "+ suma);
		System.out.println("La resta de 3 valores doubles es: "+ resta);
		System.out.println("La multiplicación de 3 valores doubles es: "+ mult);
		System.out.println("La división de 3 valores doubles es: "+ div);
		
	
	}

}
